mapper.select:
select ID,
    MATCH_NO,
    HOST_TEAM_NO,
    HOST_SHORT_NAME,
    GUEST_TEAM_NO,
    GUEST_SHORT_NAME,
    LEAGUE_NO,
    SEASON_NO,
    SEASON_NAME,
    MATCH_TIME,
    ROUND,
    HOST_GOAL,
    GUEST_GOAL,
    HALF_TIME_HOST_GOAL,
    HALF_TIME_GUEST_GOAL,
    MATCH_STATUS
from T_MATCH

	MATCH_NO = #{matchNo}
	and HOST_TEAM_NO = #{hostTeamNo}
	and GUEST_TEAM_NO = #{guestTeamNo}
	and LEAGUE_NO = #{leagueNo}
	and SEASON_NAME = #{seasonName}
	and MATCH_TIME >= #{beginDate}
	and MATCH_TIME < #{endDate}
	and ROUND = #{round}
	and MATCH_STATUS = #{matchStatus}

  	select 

  	from t_match 
    left join t_league_info
    on t_match.league_no = t_league_info.league_no

    	MATCH_NO = #{matchNo}
			and HOST_TEAM_NO = #{hostTeamNo}
			and GUEST_TEAM_NO = #{guestTeamNo}
			and t_match.LEAGUE_NO = #{leagueNo}
			and SEASON_NAME = #{seasonName}
			and MATCH_TIME = #{matchTime}
			and ROUND = #{round}
			and MATCH_STATUS = #{matchStatus}

select

,LEAGUE_SHORT_NAME
from t_match 
left join t_league_info
on t_match.league_no = t_league_info.league_no

	and MATCH_STATUS = #{matchStatus}
	and MATCH_TIME  <  #{matchTime}
	and LEAGUE_NO = #{leagueNo}
	and SEASON_NO = #{seasonNo}
	and(
		(HOST_TEAM_NO = #{hostTeamNo} and GUEST_TEAM_NO = #{guestTeamNo}) 
		or
		(GUEST_TEAM_NO = #{hostTeamNo} and HOST_TEAM_NO = #{guestTeamNo}) 
		)

order by MATCH_TIME DESC

    select

    ,LEAGUE_SHORT_NAME
    from t_match 
    left join t_league_info
    on t_match.league_no = t_league_info.league_no

    	and MATCH_STATUS = #{matchStatus}
    	and MATCH_TIME  <  #{matchTime}
    	and LEAGUE_NO = #{leagueNo}
    	and SEASON_NO = #{seasonNo}

	    	and (
	    		HOST_TEAM_NO = #{hostTeamNo} or GUEST_TEAM_NO = #{hostTeamNo}
	    		)


    order by MATCH_TIME DESC

select ID,
    MATCH_NO,
    HOST_TEAM_NO,
    HOST_SHORT_NAME,
    GUEST_TEAM_NO,
    GUEST_SHORT_NAME,
    LEAGUE_NO,
    SEASON_NO,
    SEASON_NAME,
    MATCH_TIME,
    ROUND,
    HOST_GOAL,
    GUEST_GOAL,
    HALF_TIME_HOST_GOAL,
    HALF_TIME_GUEST_GOAL,
    MATCH_STATUS
from T_MATCH

	MATCH_NO = #{matchNo}
	and HOST_TEAM_NO = #{hostTeamNo}
	and GUEST_TEAM_NO = #{guestTeamNo}
	and LEAGUE_NO = #{leagueNo}
	and SEASON_NO = #{seasonNo}
	and SEASON_NAME = #{seasonName}
	and ROUND = #{round}
	and MATCH_STATUS = #{matchStatus}
